(1090, 365, 'Yes', 'greenbullet.png', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">&nbsp;Since you mentioned you have an efficient inventory management system, we are assuming you are getting the following benefits from it:</span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Increased Sales</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Shorter Lead Times</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Improved Delivery Performance</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Lower Costs</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Accurate Planning</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Increased Employee Efficiency</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Decreased Stock-Outs</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Increased Customer Loyalty</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Increased Inventory Turnover</span></span></li>\r\n</ol>\r\n', '20', 'Inventory Management System'),
(1091, 365, 'No', 'redbullet.png', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">It is imperative to have an efficient inventory management system as it provides the following benefits:</span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Increased Sales</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Shorter Lead Times</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Improved Delivery Performance</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Lower Costs</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Accurate Planning</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Increased Employee Efficiency</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Decreased Stock-Outs</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Increased Customer Loyalty</span></span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Increased Inventory Turnover</span></span></li>\r\n</ol>\r\n', '8', 'Inventory Management System'),
