(1492, 503, 'Inadequate sales\r\n', 'yellowbullet.png', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">You have mentioned that Inadequate Sales is one of the current financial risks in your business. You can do the following to mitigate the risks:</span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Hire the right people</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Invest in sales training</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Use the right sales structure</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Refresh your sales strategy</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Make decisions with data</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Find a methodology that works</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Elevate your customer experience</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Activate multiple sales channel</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Build a robust referral network</span></span></li>\r\n</ol>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">In order to know exactly how to boost sales and multiply your profits, get in touch with one of our experts.</span></span></p>\r\n', '3', ''),
(1493, 503, 'Non-availability of adequate bank funding\r\n', 'yellowbullet.png', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">You have mentioned that Non-availability of adequate bank funding is one of the current financial risks in your business. You can do the following to mitigate the risks:</span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Identify the reason why bank funding is not available &ndash; insufficient profits, poor track record, inadequate security to offer.</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Once identified, take adequate corrective steps to fix the same</span></span></li>\r\n</ol>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">If you wish to raise funds but are not able to, kindly get in touch with one of our experts who might help you out.</span></span></p>\r\n', '3', ''),
(1494, 503, 'Liquidity\r\n', 'yellowbullet.png', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">You have mentioned that Liquidity is one of the current financial risks in your business. You can do the following to mitigate the risks:</span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Forecasting Cash Flow</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Monitoring and Optimizing Net Working Capital</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Managing Existing Credit Facilities</span></span></li>\r\n</ol>\r\n', '3', ''),
(1495, 503, 'Solvency\r\n', 'yellowbullet.png', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">You have mentioned that Solvency is one of the current financial risks in your business. You can do the following to mitigate the risks:</span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Focus on cash flow</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Reduce business expenses</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Keep your creditors in the loop</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Get good financial and legal advice</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Negotiate with your creditors</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Chase up debtors for immediate payment</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Sell off assets if possible</span></span></li>\r\n</ol>\r\n', '3', ''),
(1496, 503, 'Excessive overhead\r\n', 'yellowbullet.png', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">You have mentioned that Excessive Overhead is one of the current financial risks in your business. You can do the following to mitigate the risks:</span></span></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Find a More Cost-Effective Office Space</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Rent Instead of Buy</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Trim Your Team</span></span></li>\r\n	<li style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Outsource</span></span>\r\n	<ol>\r\n		<li><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Review Your Contracts</span></span></li>\r\n	</ol>\r\n	</li>\r\n</ol>\r\n', '3', ''),
(1497, 503, 'Non-availability of adequate credit from suppliers\r\n', 'yellowbullet.png', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">You have mentioned that Non-availability of adequate credit from suppliers is one of the current financial risks in your business. You can do the following to mitigate the risks:</span></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Diversify Suppliers</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Be Aware of Suppliers&#39; Risks</span></span></li>\r\n	<li><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Honour the credit terms to build credibility</span></span></li>\r\n</ol>\r\n', '3', ''),
(1498, 503, 'None of the above', 'yellowbullet.png', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">You have mentioned that there are no financial risks. All businesses are subject to financial risks no matter how small or big they are. It is suggested that you hire a consultant to do a complete risk assessment of your business.</span></span></p>\r\n', '30', ''),
(1499, 504, 'yes', 'greenbullet.png', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">It is critical to get the risks validated from an external consultant or an auditor. &nbsp;Risk consultants or Auditors use their business, finance, and mathematics expertise to help firms seeking to decrease or eliminate risk while performing operational, financial, and technological business processes. They help clients to identify, understand, and&nbsp;manage risks related to daily operations.</span></span></p>\r\n', '5', 'Risk Validation'),
(1500, 504, 'no', 'redbullet.png', '<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">It is critical to get the risks validated from an external consultant or an auditor. &nbsp;Risk consultants or Auditors use their business, finance, and mathematics expertise to help firms seeking to decrease or eliminate risk while performing operational, financial, and technological business processes. They help clients to identify, understand, and&nbsp;manage risks related to daily operations.</span></span></p>\r\n', '0', 'Risk Validation'),
