(541, 87, '<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Do you clearly understand your competitive marketspace and your customers buying habits?</span></span><br />\r\n&nbsp;</p>\r\n', 'binary', '', '', '', '', '', 0, NULL, NULL, 1, '2022-09-05 14:57:36', '2022-12-07 15:37:27'),
(542, 87, '<p><span style=\"font-size:16px\"><span style=\"font-family:Times New Roman,Times,serif\">Do you have a well-defined USP (Unique Selling Proposition) that differentiates you from your competitors</span></span></p>\r\n', 'binary', '', '', '', '', '', 0, NULL, NULL, 1, '2022-09-05 15:00:14', '2022-12-07 15:37:27'),
